            //     console.log(isClicked[0])
            // }

    };


    return (
        <>
        <p>{isClicked[0].clicked ? "true" : "false"}</p>
        <Background>
            <Logo src={logo}></Logo>
            <Menu>
                <ItemMenu menuId="1" onClick={() => DisplayMenu("1")}>
