import React, { useState, useEffect } from "react";
import logo from "../assets/Logos/logo.png"
import "./Header.css";

import {
    Background,
    Logo,
    Menu,
    P,
    ItemMenu,
    // PointeurMenu
} from "/Users/mariegarde/Desktop/Projet - perso/Loudher/frontend/src/styled-components/Header.jsx";

export default function Header () {
    let isClicked = [
        {clicked : true},
        {clicked : false},
        {clicked : false},
        {clicked : false},
        {clicked : false},
        {clicked : false},
    ];

    //const [Clicked, setClicked] = useState(isClicked);


        const DisplayMenu = menuId => {
            // eslint-disable-next-line react-hooks/exhaustive-deps
            //useEffect(() => {
                isClicked = [
                {clicked : false},
                {clicked : false},
                {clicked : false},
                {clicked : false},
                {clicked : false},
                {clicked : false},
            ]
        //});

            if (menuId === "1") {
                isClicked[0] = true; 
                isClicked[1] = isClicked[2] = isClicked[3] = isClicked[4] = isClicked[5] = false;
                console.log(isClicked[1])
            } else if (menuId === "2") {
                isClicked[1] = true;
                isClicked[0] = isClicked[2] = isClicked[3] = isClicked[4] = isClicked[5] = false;
                console.log(isClicked[0])
            }

    };


    return (
        <>
        <p>{isClicked[0].clicked ? "true" : "false"}</p>
        <Background>
            <Logo src={logo}></Logo>
            <Menu>
                <ItemMenu menuId="1" onClick={() => DisplayMenu("1")}>
                    <P>Accueil</P>
                    <div className={isClicked[0].clicked ? "display" : "noDisplay"}></div>
                </ItemMenu>
                <ItemMenu menuId="2" onClick={() => DisplayMenu('2')}>
                    <P>Projet</P>
                    <div className={isClicked[1].clicked ? "display" : "noDisplay"}></div>
                </ItemMenu>
                <ItemMenu menuId="3" onClick={() => DisplayMenu('3')}>
                    <P>Actions</P>
                    <div className={isClicked[2].clicked ? "display" : "noDisplay"}></div>
                </ItemMenu>
                <ItemMenu menuId="4" onClick={() => DisplayMenu('4')}>
                    <P>Annuaire</P>
                    <div className={isClicked[3].clicked ? "display" : "noDisplay"}></div>
                </ItemMenu>
                <ItemMenu menuId="5" onClick={() => DisplayMenu('5')}>
                    <P>Adh√©rer</P>
                    <div className={isClicked[4].clicked ? "display" : "noDisplay"}></div>
                </ItemMenu>
                <ItemMenu menuId="6" onClick={() => DisplayMenu('6')}>
                    <P>Contacts</P>
                    <div className={isClicked[5].clicked ? "display" : "noDisplay"}></div>
                </ItemMenu>
            </Menu>
        </Background>
        </>

    )
}
