import React, { Component } from "react";
import logo from "../assets/Logos/logo.png"
import "./Header.css";

import {
    Background,
    Logo,
    Menu,
    P,
    ItemMenu,
    // PointeurMenu
} from "/Users/mariegarde/Desktop/Projet - perso/Loudher/frontend/src/styled-components/Header.jsx";

export default function Header () {

    let isClicked1 = true;
    let isClicked2 = false;
    let isClicked3 = false;
    let isClicked4 = false;
    let isClicked5 = false;
    let isClicked6 = false;

    const displayMenu = menuId => {
        switch (menuId) {
            case '1' :
                return isClicked1 = true && (isClicked2 = isClicked3 = isClicked4 = isClicked5 = isClicked6 = false);
            case '2' :
                return (
                    isClicked2 = true && (isClicked1 = isClicked3 = isClicked4 = isClicked5 = isClicked6 = false),
                    console.log(isClicked1),
                    console.log(isClicked2),
                    console.log(isClicked3),
                    console.log(isClicked4),
                    console.log(isClicked5),
                    console.log(isClicked6));
            case '3' :
                return console.log("on y est aussi 3!")
            case '4' :
                return console.log("on y est aussi 4!")
            case '5' :
                return console.log("on y est aussi 5!")
            case '6' :
                return console.log("on y est aussi 6!")
            default :
                return console.log("chelou")
        } 
    };




    return (
        <Background>
            <Logo src={logo}></Logo>
            <Menu>
                <ItemMenu menuId="1" onClick={() => displayMenu('1')}>
                    <P>Accueil</P>
                    <div className={isClicked1 ? "display" : "noDisplay"}></div>
                </ItemMenu>
                <ItemMenu menuId="2" onClick={() => displayMenu('2')}>
                    <P>Projet</P>
                    <div className={isClicked2 ? "display" : "noDisplay"}></div>
                </ItemMenu>
                <ItemMenu menuId="3" onClick={() => displayMenu('3')}>
                    <P>Actions</P>
                    <div className={isClicked3 ? "display" : "noDisplay"}></div>
                </ItemMenu>
                <ItemMenu menuId="4" onClick={() => displayMenu('4')}>
                    <P>Annuaire</P>
                    <div className={isClicked4 ? "display" : "noDisplay"}></div>
                </ItemMenu>
                <ItemMenu menuId="5" onClick={() => displayMenu('5')}>
                    <P>Adhérer</P>
                    <div className={isClicked5 ? "display" : "noDisplay"}></div>
                </ItemMenu>
                <ItemMenu menuId="6" onClick={() => displayMenu('6')}>
                    <P>Contacts</P>
                    <div className={isClicked6 ? "display" : "noDisplay"}></div>
                </ItemMenu>
            </Menu>
        </Background>

    )
}
