import styled from "styled-components";

export const Background = styled.div`
    height : 25vh;
    width : 100%;
    background-color : #d2d2d2;
    top : 0;
`;

export const Logo = styled.img`
    height : 14vh;
    display: block;
    margin-right : auto;
    margin-left: auto;
    padding-top : 3vh;
`;

export const Menu = styled.div`
    display : flex;
    justify-content : center;
    margin-top : 3vh;
`;

export const P = styled.p`
    padding-right : 3vw;
    padding-left: 3vw;
    cursor: pointer;
`;
